"use strict";

import resolve from "resolve";
import eslintBase from "eslint";
import path from "path";

export default async function requireFromESLint(id) {
  const baseDir = path.dirname(require.resolve("eslint"));
  const resolvedPath = resolve.sync(id, { basedir: baseDir });
  const module = await import(resolvedPath);
  return module.default || module;
}
